import React, {useState, useEffect} from "react";

function Products() {

    const [data, setData] = useState([]);
    const[filter,setFilter] = useState(data);
    const[loading,setLoading] =useState(false);

    let ComponentMounted = true;

    useEffect(() => {
        const getProducts = async() =>{
            setLoading(true);
            const response = await fetch ("https://fakestoreapi.com/products");
            if(ComponentMounted){
                setData(await response.clone().json());
                setFilter(await response.json());
                setLoading(false);
                console.log(filter)
            }

            return () =>{
                ComponentMounted = false;
            }
        }
        getProducts()
    },[])

    const Loading = () => {
        return (
            <>
            Loading...
            </>
        )
    }

    const ShowProducts = ()=>{
        return(
            <>
        <div className="buttons d-flex justify-content-center mb-5 pd-5">
            <div className="btn btn-outline-dark me-2">All</div>
            <div className="btn btn-outline-dark me-2">Men's Clothes</div>
            <div className="btn btn-outline-dark me-2">Women's Clothes</div>
            <div className="btn btn-outline-dark me-2">Electronic</div>

        </div>
        </>
        )
    }

    return(
        <div>
            <div className="container my-5 py-5">
                <div className="row">
                    <div className="col-12 mb-5">
                        <h1 className="display-6 fw-bolder text-center">Lateset Products</h1>
                        <hr/>
                    </div>
                    </div> 
                    <div className="row justify-content-center">{loading ? <Loading/> : <ShowProducts/>}</div>

            </div>
        </div>
    )
} 

export default Products;
